import React, { useState } from "react";
import FeedbackList from "./components/FeedbackList";
import FeedbackStats from "./components/FeedbackStats";
import Header from "./components/Header";
import FeedbackData from "./data/FeedbackData";
import FeedbackForm from "./data/FeedbackForm";

function App() {

    const [feedback, setFeeback] = useState(FeedbackData);

    const deleteFeeback = (id) => {
        if(window.confirm('Are you sure you want to delete?')){
            setFeeback(feedback.filter((item) => item.id !== id));
        }
    }

    return (
        <>
            <Header />
            <div className='container'>
                <FeedbackForm />
                <FeedbackStats feedback={feedback} />
                <FeedbackList feedback={feedback} handleDelete={deleteFeeback}/>
            </div>
        </>
    )
}

export default App
